package jd04_selection_statements_task;public class Calculator {    public static void main(String[] args) {        double firstNumber = 100,                secondNumber = 20;        char operator = '*';        switch (operator) {            case '+':                System.out.println("Addition: " + (firstNumber + secondNumber));                break;            case '-':                System.out.println("Subtraction: " + (firstNumber - secondNumber));                break;            case '*':                System.out.println("Multiplication: " + (firstNumber * secondNumber));                break;            case '/':                System.out.println("Division: " + (firstNumber / secondNumber));                break;            default:                System.out.println("Not Valid Input");        }    }}